import React from 'react';
import '../style/Dashboard.css';
// Import your images like this:
import mainImage from '../images/Dashboard.jpg';
import featuredImage from '../images/cupcakes.jpeg';

const Dashboard = () => {
  return (
    <div className="dashboard">
      

      
        <h1>BakeHub</h1>
        <p align>The Best Bakery in Town</p>
      
      <div className="main-image">
        {/* Use the imported image or a placeholder */}
        <img src={mainImage } alt="Various baked goods" />
        <button className="shop-now-btn">SHOP NOW</button>
      </div>

      <h2>Featured Items</h2>

      <div className="featured-section">
        <div className="featured-image">
          {/* Use the imported image or a placeholder */}
          <img src={featuredImage } alt="Featured baked goods" />
        </div>
        <div className="featured-text">
          <p>
            Our exquisite chocolate cupcakes are a true delight for chocolate lovers. 
            Made with rich, moist cake and topped with smooth, creamy frosting, 
            these cupcakes are perfect for any occasion. Whether you're celebrating 
            a special event or just treating yourself, our cupcakes are sure to satisfy 
            your sweet tooth.
          </p>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
