import React, { useState ,} from 'react';
import { useParams } from 'react-router-dom';
import '../style/buynow.css'; // Adjust path as per your project structure

import { useCart } from '../context/CartContext'; // Adjust import based on your actual path

// Sample cake data (replace with your actual data or import from a data source)
const cakes = [
  {
    id: 1,
    image: '../image/strawberry.jpeg',
    name: 'Strawberry Cake',
    description: 'Delicious strawberry flavored cake',
    price: 1045, // Price per pound
  },
  {
    id: 2,
    image: '../images/vanilla',
    name: 'Vanilla Cake',
    description: 'Classic vanilla cake with creamy frosting',
    price: 950,
  },
  // Add more cakes as needed
];

const BuyNowPage = () => {
  const { id } = useParams(); // Assuming your route provides the ID, e.g., /cakes/1
  const { addToCart } = useCart(); // Assuming you have a CartContext to manage cart functionality

  // Find the cake with ID 1 (simulated)
  const selectedCake = cakes.find(cake => cake.id === 1);

  const [quantity, setQuantity] = useState(1);
  const [message, setMessage] = useState('');
  const [flavor, setFlavor] = useState('');
  const [accessories, setAccessories] = useState([]);

  if (!selectedCake) {
    return <div>Loading...</div>; // Replace with proper loading indicator or error handling
  }

  const handleAddToCart = () => {
    const item = {
      ...selectedCake,
      quantity,
      message,
      flavor,
      accessories,
    };
    addToCart(item);
    alert('Cake added to cart!'); // Replace with proper feedback mechanism
  };

  const handleBuyNow = () => {
    handleAddToCart(); // Example: Add to cart before redirecting
    // Redirect to cart or checkout page
  };

  const handleQuantityChange = (operation) => {
    if (operation === 'increment') {
      setQuantity(quantity + 1);
    } else if (operation === 'decrement' && quantity > 1) {
      setQuantity(quantity - 1);
    }
  };

  return (
    <div className="buynow-container">
      <div className="cake-details">
        <div className="cake-image-container">
          <img src={selectedCake.image} alt={selectedCake.name} className="cake-image" />
        </div>
        <div className="cake-info">
          <h2>{selectedCake.name}</h2>
          <p>{selectedCake.description}</p>
          <p className="price">Price per pound: Rs {selectedCake.price}</p>
          <div className="quantity-selector">
            <button onClick={() => handleQuantityChange('decrement')}>-</button>
            <span>{quantity}</span>
            <button onClick={() => handleQuantityChange('increment')}>+</button>
          </div>
          <div className="total-price">
            <strong>Total Price: Rs {selectedCake.price * quantity}</strong>
          </div>
        </div>
      </div>
      <div className="customization">
        <h3>Customize Your Cake</h3>
        <div className="message">
          <label>Message on Cake:</label>
          <textarea value={message} onChange={(e) => setMessage(e.target.value)} />
        </div>
        <div className="flavor">
          <label>Flavor:</label>
          <select value={flavor} onChange={(e) => setFlavor(e.target.value)}>
            <option value="">Select Flavor</option>
            <option value="Chocolate">Chocolate</option>
            <option value="Vanilla">Vanilla</option>
            <option value="Strawberry">Strawberry</option>
          </select>
        </div>
        <div className="accessories">
          <label>Accessories:</label>
          <select multiple value={accessories} onChange={(e) => setAccessories(Array.from(e.target.selectedOptions, option => option.value))}>
            <option value="Sprinkles">Sprinkles</option>
            <option value="Candles">Candles</option>
            <option value="Chocolate Drizzle">Chocolate Drizzle</option>
          </select>
        </div>
      </div>
      <div className="actions">
        <button className="buy-now" onClick={handleBuyNow}>Buy Now</button>
        <button className="add-to-cart" onClick={handleAddToCart}>Add to Cart</button>
      </div>
    </div>
  );
};

export default BuyNowPage;
