// Repairs.jsx

import React, { useState } from 'react';
import './Repair.css';
import userEvent from '@testing-library/user-event';

const Repairs = () => {
    const [repairTitle, setRepairTitle] = useState('');
    const [repairDescription, setRepairDescription] = useState('');
    const [repairBiketype, setRepairBiketype] = useState('');
    const [repairCost, setRepairCost] = useState('');
    const [repairRequests, setRepairRequests] = useState([]);

    const handleSubmit = (e) => {
        e.preventDefault();

        // Validate form fields
        if (!repairTitle || !repairDescription || !repairBiketype || !repairCost) {
            alert('All fields are required');
            return;
        }

        // Create repair request object
        const newRepairRequest = {
            title: repairTitle,
            description: repairDescription,
            biketype: repairBiketype,
            cost: repairCost,
        };

        // Update the list of repair requests
        setRepairRequests((prevRequests) => [...prevRequests, newRepairRequest]);

        // Clear the form fields
        setRepairTitle('');
        setRepairDescription('');
        setRepairBiketype('');
        setRepairCost('');
    };

    return (
        <div className="repairs-container">
            <h1>Repairs</h1>

            <form onSubmit={handleSubmit} className="repair-form">
                <label>
                    Title:
                    <select onChange={(e) => setRepairTitle(e.target.value)} className='form-control mb-2'>
                        <option value="CHAIN LUBE & CLEANER KIT- 500ML">CHAIN LUBE & CLEANER KIT- 500ML</option>
                        <option value="LIQUID GUN SEMI SYNTHETIC-15W50- 210 LTS">LIQUID GUN SEMI SYNTHETIC-15W50- 210 LTS</option>
                        <option value="FRONT FORK OIL -500 ML PACK">FRONT FORK OIL -500 ML PACK</option>
                        <option value="BRAKE OIL DOT 4-100 ML">BRAKE OIL DOT 4-100 ML</option>
                        <option value="RE-SEALANT- 10 GMS">RE-SEALANT- 10 GMS</option>
                        <option value="PIPE OUTLET">PIPE OUTLET</option>
                        <option value="FLANGE">FLANGE</option>
                    </select>
                </label>

                <label>
                    Description:
                    <textarea value={repairDescription} onChange={(e) => setRepairDescription(e.target.value)} />
                </label>

                <label>
                    Bike Type:
                    <select onChange={(e) => setRepairBiketype(e.target.value)} className='form-control mb-2'>
                        <option value="Thunderbird 500">Thunderbird 500</option>
                        <option value="Classic 350">Classic 350</option>
                        <option value="Meteor 350">Meteor 350</option>
                        <option value="Hunter 350">Hunter 350</option>
                        <option value="Scram 411">Scram 411</option>
                        <option value="Himalayan">Himalayan</option>
                    </select>
                </label>

                <label>
                    Cost:
                    <input type="number" value={repairCost} onChange={(e) => setRepairCost(e.target.value)} />
                </label>

                <button type="submit">Submit Repair Request</button>
            </form>

            <div className="repair-requests">
                <h2>Repair Requests:</h2>

                <ul>
                    {repairRequests.map((request, index) => (
                        <li key={index}>
                            <strong>{request.title}</strong>
                            <p>{request.description}</p>
                            <p>{request.biketype}</p>
                            <p>Cost: {request.cost}</p>
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
};

export default Repairs;
