import React from 'react';
import '../style/Dashboard.css';
// Import your images like this:
import mainImage from '../images/Dashboard.jpg';
import featuredImage from '../images/cupcakes.jpeg';
import featuredImage2 from '../images/wedding.jpg';


const Dashboard = () => {
    return (
        <div className="dashboard">


            <div className='bake'>
                <h1>BakeHub</h1>
                <p align='center'>The Best Bakery in Town</p>
            </div>

            <div className="main-image-container">
                <img
                    src={mainImage}
                    alt="Various baked goods"
                    className="main-image"
                />
                <button className="shop-now-btn">SHOP NOW</button>
            </div>

            <h2>Featured Items</h2>

            <div className="featured-section-1">
                <div className="featured-image-1">
                    {/* Use the imported image or a placeholder */}
                    <img src={featuredImage} alt="Featured baked goods" />
                </div>
                <div className="featured-text">
                    <p>
                        Our exquisite chocolate cupcakes are a true delight for chocolate lovers.
                        Made with rich, moist cake and topped with smooth, creamy frosting,
                        these cupcakes are perfect for any occasion. Whether you're celebrating
                        a special event or just treating yourself, our cupcakes are sure to satisfy
                        your sweet tooth.
                    </p>
                </div>
            </div>

            <div className="featured-section-2">
                <div className="featured-image-2">
                    {/* Use the imported image or a placeholder */}
                    <img src={featuredImage2} alt="Featured baked goods" />
                </div>
                <div className="featured-text-2">
                    <p>
                    Wedding cakes are traditional centerpiece desserts, often multi-tiered and elaborately decorated, 
                    symbolizing celebration and unity in marriage ceremonies. They can be customized to reflect the couple's taste and wedding theme, ranging from classic white designs to modern, themed creations. Couples frequently incorporate unique flavors, fillings, and decorations to make their wedding cake a memorable and personal highlight of their special day.
                    </p>
                </div>
            </div>
        </div>
    );
};

export default Dashboard;
