// AddToCartPage.js

import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import '../style/cart.css'; // Assuming you have a cart.css file for styling

const CartItem = ({ cake, onDelete }) => (
  <div className="cart-item">
    <img src={cake.image} alt={cake.name} className="cart-item-image" />
    <div className="cart-item-details">
      <h3>{cake.name}</h3>
      <button className="delete-item" onClick={() => onDelete(cake.id)}>
        <i className="fa fa-trash-o"></i> Delete
      </button>
    </div>
  </div>
);

const CartTotal = ({ totalPrice }) => (
  <div className="cart-total">
    <h3>Total Price</h3>
    <p>Rs {totalPrice}</p>
    <Link to="/checkout"><button className="buy-now">Buy Now</button></Link>
  </div>
);

const AddToCart = ({ cartItems, onDelete }) => {
  const total = cartItems.reduce((acc, cake) => acc + parseInt(cake.price), 0);

  return (
    <div className="page">
      <h2>My Cart</h2>
      <div className="cart-items">
        {cartItems.map((cake) => (
          <CartItem key={cake.id} cake={cake} onDelete={onDelete} />
        ))}
      </div>
      <CartTotal totalPrice={total} />
    </div>
  );
};

export default AddToCart;
