import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import '../styles/buynow.css'; // Adjust path as per your project structure

const BuyNowPage = ({ cakes }) => {
  const { id } = useParams(); // Assuming you're using useParams to get cake id
  const [pounds, setPounds] = useState(1); // Initial pounds
  const [message, setMessage] = useState('');
  const [flavour, setFlavour] = useState('');
  const [accessory, setAccessory] = useState('');
  
  // Find the selected cake from the cakes array based on id
  const selectedCake = cakes.find(cake => cake.id === parseInt(id));

  // Function to calculate total price based on pounds
  const calculateTotalPrice = () => {
    return selectedCake.price * pounds;
  };

  // Handle increase and decrease pounds
  const handleIncreasePounds = () => {
    setPounds(prev => prev + 1);
  };

  const handleDecreasePounds = () => {
    if (pounds > 1) {
      setPounds(prev => prev - 1);
    }
  };

  // Placeholder for handling buy now and add to cart actions
  const handleBuyNow = () => {
    // Implement buy now logic here
    alert(`Buy Now clicked for ${selectedCake.name}`);
  };

  const handleAddToCart = () => {
    // Implement add to cart logic here
    alert(`Add to Cart clicked for ${selectedCake.name}`);
  };

  if (!selectedCake) {
    return <div>Loading...</div>; // Handle case where cake is not found
  }

  return (
    <div className="buynow-container">
      <div className="left-container">
        <img src={selectedCake.image} alt={selectedCake.name} className="cake-image" />
      </div>
      <div className="right-container">
        <h2>{selectedCake.name}</h2>
        <p>{selectedCake.description}</p>
        <p>Price per pound: Rs {selectedCake.price}</p>
        <div className="pounds-container">
          <label>Pounds: </label>
          <button onClick={handleDecreasePounds}>-</button>
          <span>{pounds}</span>
          <button onClick={handleIncreasePounds}>+</button>
        </div>
        <p>Total Price: Rs {calculateTotalPrice()}</p>
        <div className="message-container">
          <label htmlFor="message">Message on Cake:</label>
          <input
            type="text"
            id="message"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
          />
        </div>
        <div className="flavours-container">
          <label htmlFor="flavour">Flavours to add:</label>
          <select
            id="flavour"
            value={flavour}
            onChange={(e) => setFlavour(e.target.value)}
          >
            <option value="">Select flavour</option>
            <option value="Chocolate">Chocolate</option>
            <option value="Vanilla">Vanilla</option>
            <option value="Strawberry">Strawberry</option>
          </select>
        </div>
        <div className="accessories-container">
          <label htmlFor="accessory">Extra accessories:</label>
          <select
            id="accessory"
            value={accessory}
            onChange={(e) => setAccessory(e.target.value)}
          >
            <option value="">Select accessory</option>
            <option value="Candles">Candles</option>
            <option value="Cake Topper">Cake Topper</option>
            <option value="Edible Decorations">Edible Decorations</option>
          </select>
        </div>
        <div className="button-container">
          <button onClick={handleBuyNow}>Buy Now</button>
          <button onClick={handleAddToCart}>Add to Cart</button>
        </div>
      </div>
    </div>
  );
};

export default BuyNowPage;
